package com.assignment3;

public class Program3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 77;
        double numd = 77.55d;
        float numf = 1.5f;
        
        double result1 = num + numd;
        System.out.println("Result of int + double: " + result1);
        
        double result2 = num - numd;
        System.out.println("Result of int - double: " + result2);
        
        double result3 = num * numd;
        System.out.println("Result of int * double: " + result3);
        
        double result4 = num / numd;
        System.out.println("Result of int / double: " + result4);
        
        float result5 = num + numf;
        System.out.println("Result of int + float: " + result5);
        
        float result6 = num - numf;
        System.out.println("Result of int - float: " + result6);
        
        float result7 = num * numf;
        System.out.println("Result of int * float: " + result7);
        
        float result8 = num / numf;
        System.out.println("Result of int / float: " + result8);
        
        double result9 = numd + numf; 
        System.out.println("Result of double = float: " + result9);
        
        double result10 = numd - numf; 
        System.out.println("Result of double - float: " + result10);
        
        double result11 = numd * numf; 
        System.out.println("Result of double * float: " + result11);
        
        double result12 = numd / numf; 
        System.out.println("Result of double * float: " + result12);
        
	}

}
